import { useState } from "react";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);

  const addTask = () => {
    const taskValue = document.querySelector(".task-input").value;
    const task = {
      value: taskValue,
      isComplete: false,
    };
    const newTasks = [...tasks, task];
    setTasks(newTasks);
    console.log("tasks: ", newTasks);
  };

  const deleteTask = (taskIndex) => {
    const currentTasks = [...tasks];
    const updatedTasks = currentTasks.filter((task, idx) => idx !== taskIndex);
    setTasks(updatedTasks);
  };

  const markAsCompleted = (taskIndex) => {
    const currentTasks = [...tasks];
    const completedTaskIndex = currentTasks.findIndex(
      (task, idx) => idx === taskIndex
    );
    currentTasks[completedTaskIndex] = {
      ...currentTasks[completedTaskIndex],
      isComplete: true,
    };
    setTasks(currentTasks);
  };

  const incompleteTasks = tasks.filter((task) => !task.isComplete);
  const completedTasks = tasks.filter((task) => task.isComplete);

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <div className="input-container">
        <input
          className="task-input"
          type="text"
          placeholder="Enter a task"
        ></input>
        <button onClick={addTask}>Add Task</button>
      </div>
      <div className="tasks-container">
        <div className="task-header">Incomplete Tasks</div>
        {incompleteTasks.map((task, idx) => {
          return (
            <div className="task" key={task}>
              {task.value}{" "}
              <button onClick={() => markAsCompleted(idx)}>
                Mark As Completed
              </button>
              <button onClick={() => deleteTask(idx)}>Delete Task</button>
            </div>
          );
        })}
      </div>
      <div className="completed-tasks-container">
        <div className="task-header">Completed Tasks</div>
        {completedTasks.map((task, idx) => {
          return (
            <div className="task" key={task}>
              {task.value}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
